mapper.select:
SELECT c.title title ,c.id unitid,


        sum(CASE rec.checkitemid WHEN ${item.id} THEN rec.checkvalue ElSE 0 END)  "${item.id}"


from t_tb_company c left join td_infosrec rec on c.id=rec.unitid LEFT JOIN td_infos m  ON rec.infosid = m.id
and  m.mtime >= #{sreachDto.beforeTime,jdbcType=TIMESTAMP}
and  m.mtime <= #{sreachDto.afterTime,jdbcType=TIMESTAMP}
and  m.creatorid = #{sreachDto.creatorid}
and  m.title like CONCAT('%',#{sreachDto.title},'%') 
and  m.status = #{sreachDto.status}
GROUP BY c.id ORDER BY c.id asc, rec.createtime
